import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNarbar from './myNavbar';
import Image from 'react-bootstrap/Image'

const Home = () => {

  const getWindowDimensions = () => {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height
    };
  };

  const useWindowDimensions = () => {
    let [ windowDimensions, setWindowDimensions ] = useState(getWindowDimensions());

    useEffect(() => {
      const handleResize = () => {
        setWindowDimensions(getWindowDimensions());
      };

      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }, []);

    return windowDimensions;
  }

  console.log(useWindowDimensions().width);

  return (
    <>
      <MyNarbar />
      {(useWindowDimensions().width > 1000) ? 
        <p style={{ fontSize: "2em",display: "inline", position: "relative", top: "10em", left: "10em", zIndex: "2"}}>Web developer. Explorer. Life long learner.</p>
        :
        <p style={{ display: "inline", position: "relative", top: "10em", left: "10em", zIndex: "2"}}>Web developer. Explorer. Life long learner.</p>
      }
      <Image
        src="explorer.png"
        width="100%"
      />
    </>
  );
}

export default Home;
